// home.js
import React, { useState } from "react";

const reactjs = () => {
  // React state for form inputs and uploaded images


  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();

    // Perform the necessary actions for image upload, such as sending a request to your server.
    // You can use the 'name', 'desc', and 'image' state values in this function.

    // Reset the form after submission
    setName("");
    setDesc("");
    setImage(null);

    // Update the list of uploaded images (replace this with actual logic)
    setUploadedImages([
      ...uploadedImages,
      {
        name: name,
        desc: desc,
        img: { contentType: "image/png", data: "base64_encoded_image_data" },
      },
    ]);
  };

  return (
    <div>
      <h1>To Upload Image on MongoDB</h1>
      <hr />
      <div>
        <form onSubmit={handleSubmit} encType="multipart/form-data">
          <div>
            <label htmlFor="name">Image Title</label>
            <input
              type="text"
              id="name"
              placeholder="Name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              name="name"
              required
            />
          </div>
          <div>
            <label htmlFor="desc">Image Description</label>
            <textarea
              id="desc"
              name="desc"
              value={desc}
              onChange={(e) => setDesc(e.target.value)}
              rows="2"
              placeholder="Description"
              required
            ></textarea>
          </div>
          <div>
            <label htmlFor="image">Upload Image</label>
            <input
              type="file"
              id="image"
              name="image"
              onChange={(e) => setImage(e.target.files[0])}
              required
            />
          </div>
          <div>
            <button type="submit">Submit</button>
          </div>
        </form>
      </div>

      <hr />

      <h1>Uploaded Images</h1>
      <div>
        {uploadedImages.map((image, index) => (
          <div key={index}>
            <div>
              <img
                src={`data:image/${image.img.contentType};base64,${image.img.data}`}
                alt={`Uploaded Image ${index + 1}`}
              />
              <div>
                <h5>{image.name}</h5>
                <p>{image.desc}</p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default reactjs;
