import React, { useState } from "react";

const ImageUpload = ({ items }) => {
  const [name, setName] = useState("");
  const [desc, setDesc] = useState("");
  const [image, setImage] = useState(null);

  const handleNameChange = (e) => {
    setName(e.target.value);
  };

  const handleDescChange = (e) => {
    setDesc(e.target.value);
  };

  const handleImageChange = (e) => {
    setImage(e.target.files[0]);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Perform the necessary actions for image upload, such as sending a request to your server.
    // You can use the 'name', 'desc', and 'image' state values in this function.

    // Reset the form after submission
    setName("");
    setDesc("");
    setImage(null);
  };

  return (
    <div>
      <h1>To Upload Image on MongoDB</h1>
      <hr />
      <div>
        <form onSubmit={handleSubmit} encType="multipart/form-data">
          <div>
            <label htmlFor="name">Image Title</label>
            <input
              type="text"
              id="name"
              placeholder="Name"
              value={name}
              onChange={handleNameChange}
              name="name"
              required
            />
          </div>
          <div>
            <label htmlFor="desc">Image Description</label>
            <textarea
              id="desc"
              name="desc"
              value={desc}
              onChange={handleDescChange}
              rows="2"
              placeholder="Description"
              required
            ></textarea>
          </div>
          <div>
            <label htmlFor="image">Upload Image</label>
            <input
              type="file"
              id="image"
              name="image"
              onChange={handleImageChange}
              required
            />
          </div>
          <div>
            <button type="submit">Submit</button>
          </div>
        </form>
      </div>

      <hr />

      <h1>Uploaded Images</h1>
      <div>
        {items.map((image, index) => (
          <div key={index}>
            <div>
              <img
                src={`data:image/${image.img.contentType};base64,${image.img.data.toString(
                  "base64"
                )}`}
                alt={`Uploaded Image ${index + 1}`}
              />
              <div>
                <h5>{image.name}</h5>
                <p>{image.desc}</p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ImageUpload;
