import React, { useState, useEffect } from "react";
import ImageUpload from "./ImageUpload";

function App() {
  const [items, setItems] = useState([]);

  // Assume you have a function to fetch images from your server
  const fetchImages = async () => {
    try {
      const response = await fetch("/api/images");  // Adjust the endpoint as needed
      const data = await response.json();
      setItems(data);
    } catch (error) {
      console.error("Error fetching images:", error);
    }
  };

  // Fetch images when the component mounts
  useEffect(() => {
    fetchImages();
  }, []);

  return (
    <div>
      <header>
        <h1>ScribbleBook</h1>
      </header>
      <main>
      <div>
      <h1>To Upload Image on MongoDB</h1>
      <hr />
      <div>
        <form onSubmit={handleSubmit} encType="multipart/form-data">
          <div>
            <label htmlFor="name">Image Title</label>
            <input
              type="text"
              id="name"
              placeholder="Name"
              value={name}
              onChange={handleNameChange}
              name="name"
              required
            />
          </div>
          <div>
            <label htmlFor="desc">Image Description</label>
            <textarea
              id="desc"
              name="desc"
              value={desc}
              onChange={handleDescChange}
              rows="2"
              placeholder="Description"
              required
            ></textarea>
          </div>
          <div>
            <label htmlFor="image">Upload Image</label>
            <input
              type="file"
              id="image"
              name="image"
              onChange={handleImageChange}
              required
            />
          </div>
          <div>
            <button type="submit">Submit</button>
          </div>
        </form>
      </div>

      <hr />

      <h1>Uploaded Images</h1>
      <div>
        {items.map((image, index) => (
          <div key={index}>
            <div>
              <img
                src={`data:image/${image.img.contentType};base64,${image.img.data.toString(
                  "base64"
                )}`}
                alt={`Uploaded Image ${index + 1}`}
              />
              <div>
                <h5>{image.name}</h5>
                <p>{image.desc}</p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
        <ImageUpload items={items} />
      </main>
    </div>
  );
}

export default App;
